mapper.select:
select
  id, role_id, menu_id, create_time, update_time, update_user
from kaishun.tb_role_menu


        and id = #{id}


        and role_id = #{roleId}


        and menu_id = #{menuId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and update_user = #{updateUser}
