mapper.select:
select
  id, number, parent_number, name, remark, url, create_time, update_time, update_user
from kaishun.tb_menu


        and id = #{id}


        and number = #{number}


        and parent_number = #{parent_number}


        and name = #{name}


        and remark = #{remark}


        and url = #{url}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and update_user = #{updateUser}
